mapper.select:
SELECT
COUNT(*)
FROM j_customer a

    LEFT JOIN j_admin_shop jas ON jas.shopId = a.shopId

WHERE a.isDel = 16

    AND jas.adminId = #{customerPagReq.adminId}


    AND a.customerId = #{customerPagReq.customerId}


    AND a.customerType = #{customerPagReq.customerType}


    AND a.shopId = #{customerPagReq.shopId}


    AND a.customerPhone LIKE concat(concat('%',#{customerPagReq.customerPhone}),'%')



    AND a.customerName LIKE concat(concat('%',#{customerPagReq.customerName}),'%')

SELECT a.*,d1.INFO AS customerTypeInfo,d2.shopName AS shopIdInfo,d3.INFO AS customerSexInfo,d4.INFO AS warnTypeInfo,d5.INFO AS isDelInfo FROM j_customer a
LEFT JOIN j_admin_shop jas ON jas.shopId = a.shopId
LEFT JOIN j_dict_info d1 ON a.customerType = d1.ID LEFT JOIN j_shop d2 ON a.shopId = d2.shopId LEFT JOIN j_dict_info d3 ON a.customerSex = d3.ID LEFT JOIN j_dict_info d4 ON a.warnType = d4.ID LEFT JOIN j_dict_info d5 ON a.isDel = d5.ID WHERE a.isDel = 16
AND jas.adminId = #{customerPagReq.adminId}
AND a.customerId = #{customerPagReq.customerId}
AND a.customerType = #{customerPagReq.customerType}
AND a.shopId = #{customerPagReq.shopId}
AND a.customerPhone LIKE concat(concat('%',#{customerPagReq.customerPhone}),'%')
AND a.customerName LIKE concat(concat('%',#{customerPagReq.customerName}),'%')
LIMIT #{customerPagReq.page},#{customerPagReq.size}

mapper.update:
