mapper.select:
SELECT
COUNT(*)
FROM j_customer_statment
WHERE isDel = 16

	AND customerId = #{customerStatmentReq.customerId}


	AND statmentType = #{customerStatmentReq.statmentType}

SELECT a.*,d1.iNFO AS isDelInfo,d2.INFO AS statmentTypeInfo,d3.INFO AS chargeWayInfo,d4.INFO AS terminalInfo,d5.INFO AS statusInfo,d6.customerName AS customerName FROM j_customer_statment a LEFT JOIN j_dict_info d1 ON d1.ID = a.isDel LEFT JOIN j_dict_info d2 ON d2.ID = a.statmentType LEFT JOIN j_dict_info d3 ON d3.ID = a.chargeWay LEFT JOIN j_dict_info d4 ON d4.ID = a.terminal LEFT JOIN j_dict_info d5 ON d5.ID = a.status LEFT JOIN j_customer d6 ON d6.customerId = a.customerId WHERE 1=1 AND a.isDel = 16
AND a.customerId = #{customerStatmentReq.customerId}
AND a.statmentType = #{customerStatmentReq.statmentType}
ORDER BY chargeDate DESC LIMIT #{customerStatmentReq.page},#{customerStatmentReq.size}
