mapper.select:
SELECT count(*)
FROM j_nanny_info j
WHERE isDel = 16

	AND j.nannyId = #{nannyInfoReq.nannyId}


	AND j.nannyName LIKE ${%nannyInfoReq.nannyName%}


	AND j.nannyPhone = #{nannyInfoReq.nannyPhone}


	AND j.nannyStatus = #{nannyInfoReq.nannyStatus}


	AND j.nannyType = #{nannyInfoReq.nannyType}


	AND j.nannyLevel = #{nannyInfoReq.nannyLevel}

SELECT j.nannyId,j.nannyName,j.nannyPhone,j.createTime,j.nannyStatus,jti.INFO AS nannyStatusInfo,j.nannyAge,j.remark
FROM j_nanny_info j
LEFT JOIN j_dict_info jti ON j.nannyStatus = jti.ID
WHERE j.isDel = 16

	AND j.nannyId = #{nannyInfoReq.nannyId}


	AND j.nannyName LIKE ${%nannyInfoReq.nannyName%}


	AND j.nannyPhone = #{nannyInfoReq.nannyPhone}


	AND j.nannyStatus = #{nannyInfoReq.nannyStatus}


	AND j.nannyType = #{nannyInfoReq.nannyType}


	AND j.nannyLevel = #{nannyInfoReq.nannyLevel}

limit #{nannyInfoReq.page},#{nannyInfoReq.size}
